<script>
	import router from './../../vue/router/index.js'
	
	export default {
		data(){
			return {
			}
		},
		methods: {
			route(path) {
				return () => router.push(path);
			},
			back() {
				return () => window.history.back()
			},
			left(fn) {
				// Allows left button function to be overridden by new callback

				let self = this;
				return function(e) {
					if (self.functionleft == undefined) {
						fn(e);
					} else {
						self.functionleft(e);
					}
				}
			},
			right(fn) {
				// Allows right button function to be overridden by new callback

				let self = this;
				return function(e) {
					if (self.functionright == undefined) {
						fn(e);
					} else {
						self.functionright(e);
					}
				}
			}
		},
    	props: {
			iconleft: String,
			iconright: String,
			functionleft: Function,
			functionright: Function,
		},
	}
</script>

